import React from 'react';
import './Boxes.css';

const calculateFaceLocation = (data) => {
    const clarifaiFace = data
    const image = document.getElementById('input_image')
    const width = Number(image.width);
    const height = Number(image.height);
    return {
      leftCol: clarifaiFace.left_col * width,
      topRow: clarifaiFace.top_row * height,
      rightCol: width - (clarifaiFace.right_col * width),
      bottomRow: height - (clarifaiFace.bottom_row * height)
    }
  }

const Boxes = ({box}) => {
    var box2 = {}
    console.log("Hi")
    console.log(Object.keys(box).length)
    box2=box
    var main_div = []
    if (Object.keys(box).length > 0){
        console.log("Enter")
        console.log(box.outputs[0])
        box2 = calculateFaceLocation(box.outputs[0].data.regions[0].region_info.bounding_box)
        main_div = [
            {
                style: {
                    top:box2.topRow, 
                    right: box2.rightCol, 
                    left: box2.leftCol, 
                    bottom:box2.bottomRow
                }
            }
        ]
    }
    // [0].region_info.bounding_box
    return(
        <div>
            {main_div.map(div_i => <div style = {div_i.style} className = 'bounding-box' ></div>)}
        </div>
    )
} 

export default Boxes;
